mapper.insert:
INSERT INTO construction_project (
    company_id, name, address, province, city, district, project_manager_id, 
    start_date, expected_end_date, project_type, project_scale, 
    total_area, budget, description, status
) VALUES (
    #{companyId}, #{name}, #{address}, #{province}, #{city}, #{district}, #{projectManagerId},
    #{startDate}, #{expectedEndDate}, #{projectType}, #{projectScale},
    #{totalArea}, #{budget}, #{description}, #{status}
)

mapper.update:
UPDATE construction_project
name = #{name}, address = #{address}, province = #{province}, city = #{city}, district = #{district}, start_date = #{startDate}, expected_end_date = #{expectedEndDate}, project_type = #{projectType}, project_scale = #{projectScale}, total_area = #{totalArea}, budget = #{budget}, description = #{description}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(1)
FROM construction_project
WHERE id = #{projectId} AND project_manager_id = #{userId}

SELECT COUNT(1)
FROM labor_demand
WHERE project_id = #{projectId}

SELECT id
FROM construction_project
WHERE company_id = #{companyId}

SELECT COUNT(id)
FROM construction_project
WHERE province = #{province}

    AND city = #{city}

SELECT id
FROM construction_project
WHERE 1=1

    AND province = #{province}


    AND city = #{city}


    AND district = #{district}

AND is_deleted = 0
